$(document).ready(function() {
  $('#fullpage').fullpage({
    anchors: ['top', 'background', 'philosophy', 'courses', 'testimonials', 'rates', 'contact'],
    responsiveWidth: 400,
		responsiveHeight: 800
  });
});

$("#philo-icon").mouseover(function() {
  $(this).css("color", "#ddd");
});

$("#philo-icon").mouseout(function() {
  $(this).css("color", "#b02334");
});

$("#philo-icon").click(function() {
  if ($("#philo-top-left").css("display") == "none") {
    $("#philo-top-left").fadeIn();
  } else {
    $("#philo-top-left").fadeOut();
  }
  if ($("#philo-top-right").css("display") == "none") {
    $("#philo-top-right").fadeIn("slow");
  } else {
    $("#philo-top-right").fadeOut();
  }
  if ($("#philo-bottom").css("display") == "none") {
    $("#philo-bottom").fadeIn(700);
  } else {
    $("#philo-bottom").fadeOut();
  }
});

$(".philo-left").mouseleave(function() {
  $("#philo-top-left").fadeOut();
  $("#philo-top-right").fadeOut();
  $("#philo-bottom").fadeOut();
});
